$(document).ready(function() {
    $('#menuContact').mouseover(function() {
        $('#menuContact').css('color', 'red');
    });
    $('#menuContact').mouseout(function() {
        $('#menuContact').css('color', 'black');
    });

    $('#menuHomeContact').mouseover(function() {
        $('#menuHomeContact').css('color', 'red');
        $('#menuContact').css('color', 'white');
    });
    $('#menuHomeContact').mouseout(function() {
        $('#menuHomeContact').css('color', 'white');
        $('#menuContact').css('color', 'black');
    });

    $('#menuAbout').mouseover(function() {
        $('#menuAbout').css('color', 'red');
        $('#menuContact').css('color', 'white');
    });
    $('#menuAbout').mouseout(function() {
        $('#menuAbout').css('color', 'white');
        $('#menuContact').css('color', 'black');
    });

    $('#menuResume').mouseover(function() {
        $('#menuResume').css('color', 'red');
        $('#menuContact').css('color', 'white');
    });
    $('#menuResume').mouseout(function() {
        $('#menuResume').css('color', 'white');
        $('#menuContact').css('color', 'black');
    })
});